$(function(){$(".contactForm input,.contactForm textarea").jqBootstrapValidation({preventSubmit:true,submitError:function($form,event,errors){},submitSuccess:function($form,event){event.preventDefault();$("[type=submit]").prop("disabled",true);var name=$form.find("input[name=name]").val();var email=$form.find("input[name=email]").val();var phone=$form.find("input[name=phone]").val();var message=$form.find("input[name=message]").val();var firstName=name;if(firstName.indexOf(' ')>=0){firstName=name.split(' ').slice(0,-1).join(' ');}$.ajax({url:"././mail/contact_me.php",type:"POST",data:{name:name,phone:phone,email:email,message:message},cache:false,success:function(){$('.success').html("<div class='alert alert-success'>");$('.success > .alert-success').html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;").append("</button>");$('.success > .alert-success').append("<strong>Ваше сообщение успешно отправлено. В ближайшее время наши менеджеры свяжутся с вами! </strong>");$('.success > .alert-success').append('</div>');$("[type=submit]").prop("disabled",false);$('.contactForm').trigger("reset");},error:function(){$('.success').html("<div class='alert alert-danger'>");$('.success > .alert-danger').html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;").append("</button>");$('.success > .alert-danger').append("<strong>Приносим свои извинения, "+firstName+", но наш почтовый сервер времено не работает. Попробуйте, отправить сообщение еще раз и сообщите нам о проблеме!");$('.success > .alert-danger').append('</div>');$("[type=submit]").prop("disabled",false);$('.contactForm').trigger("reset");},})},filter:function(){return $(this).is(":visible");},});$("a[data-toggle=\"tab\"]").click(function(e){e.preventDefault();$(this).tab("show");});});$('#name').focus(function(){$('.success').html('');});